mapper.update:
update wms_inventory set del_flag=1

    id in
    #{it}

update wms_inventory
set quantity = quantity + (
case

    when id = #{it.id} then #{it.quantity}

end
),
update_time = #{updateTime},
update_by = #{userId}
where id in

    #{it.id}

mapper.select:
SELECT wi.warehouse_id, wi.item_id, sum(wi.quantity) AS quantity FROM wms_inventory wi
${ew.sqlSegment}
group by wi.warehouse_id, wi.item_id ORDER BY wi.warehouse_id, wi.item_id

SELECT wi.warehouse_id, wi.area_id, wi.item_id, sum(wi.quantity) AS quantity FROM wms_inventory wi
${ew.sqlSegment}
group by wi.warehouse_id, wi.area_id, wi.item_id ORDER BY wi.warehouse_id, wi.area_id, wi.item_id

SELECT wi.warehouse_id, wi.area_id, wi.item_id, wi.quantity FROM wms_inventory wi left join wms_item_type wit on wit.item_type_id = wi.item_id
${ew.sqlSegment}
ORDER BY wit.item_type_id, wi.item_id desc
